import { ThemeProvider, createTheme } from "@mui/material/styles";
import CssBaseline from "@mui/material/CssBaseline";
import { grey } from "@mui/material/colors";
import {
  Button,
  Typography,
  ButtonGroup,
  Container,
  Grid,
} from "@mui/material";
import Code from "./components/Code";
import { Data } from "./data";
import { useState } from "react";
import LiveSearch from "./components/LiveSearch";
import MenuItem from "@mui/material";
import FormControl from "@mui/material/FormControl";
import InputLabel from "@mui/material/InputLabel";
import Select from "@mui/material/Select";

const darkTheme = createTheme({
  palette: { primary: { main: grey[50] }, secondary: { main: grey[900] } },
  typography: {
    // fontFamily: ["Syne", "sans-serif"].join(","),
  },
});

export default function App() {
  const [type, setType] = useState("all");

  function handleType(text) {
    setType(() => text);
  }

  return (
    <>
      <ThemeProvider theme={darkTheme}>
        <CssBaseline />
        <main>
          <Typography
            variant="h3"
            color="secondary"
            level="title-sm"
            fontWeight={700}
            fontFamily={"Syne"}
            align="center"
            paddingTop={10}
            marginBottom={5}
          >
            DBMS LAB PROGRAMS
          </Typography>
        </main>
        <LiveSearch />
        <Container align="center" sx={{ marginBottom: 10 }}>
          <ButtonGroup
            variant="outlined"
            aria-label="outlined primary button group"
            color="secondary"
          >
            <FormControl fullWidth>
              <InputLabel id="demo-simple-select-label">Age</InputLabel>
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={'age'}
                label="Age"
                onChange={()=>{}}
              >
                <MenuItem value={10}>Ten</MenuItem>
                <MenuItem value={20}>Twenty</MenuItem>
                <MenuItem value={30}>Thirty</MenuItem>
              </Select>
            </FormControl>
            <Button onClick={() => handleType("all")}>ALL</Button>
            <Button onClick={() => handleType("html")}>CYCLE-1</Button>
            <Button onClick={() => handleType("css")}>CYCLE-1</Button>
            <Button onClick={() => handleType("js")}>CYCLE-1</Button>
          </ButtonGroup>
        </Container>
      </ThemeProvider>

      <Grid container spacing={2} style={{ alignItems: "stretch" }}>
        {Data.map((item) => {
          if (type === "all")
            return (
              <Grid
                item
                md={12}
                lg={6}
                sx={{ maxWidth: "100%", margin: "auto" }}
              >
                <Code title={item.title} code={item.code} />
              </Grid>
            );

          if (type === item.type)
            return (
              <Grid
                item
                md={12}
                lg={6}
                sx={{ maxWidth: "100%", margin: "auto" }}
              >
                <Code title={item.title} code={item.code} />
              </Grid>
            );
        })}
      </Grid>
    </>
  );
}
