import React, { useState, useEffect } from "react";
import TextField from "@mui/material/TextField";
import Stack from "@mui/material/Stack";
import Autocomplete from "@mui/material/Autocomplete";
import { Box } from "@mui/system";
import { ThemeProvider, createTheme } from "@mui/material/styles";
import { Data } from "../dataSQL";

const cardTheme = createTheme({
  palette: {
    background: { paper: "#fafafa" },
    primary: { main: "#212121" },
    secondary: { main: "#212121" },
  },
});

function LiveSearch() {
  const [jsonResults, setJsonResults] = useState([]);

  useEffect(() => {
    fetch("https://www.balldontlie.io/api/v1/players")
      .then((response) => response.json())
      .then((json) => setJsonResults(json.data));
  }, []);

  return (
    <ThemeProvider theme={cardTheme}>
      <Stack sx={{ width: '80%', marginX: 'auto', marginBottom:5 }} >
        <Autocomplete
          id="nba_palyer_demo"
          getOptionLabel={(Data) =>
            `${Data.title}`
          }
          options={Data}
          sx={{ width: '80%', marginX: 'auto'}}
          isOptionEqualToValue={(option, value) =>
            option.title === value.title
          }
          noOptionsText={"NONE"}
          renderOption={(props, jsonResults) => (
            <Box component="li" {...props} key={jsonResults.id}>
              {jsonResults.first_name} {jsonResults.last_name}
            </Box>
          )}
          renderInput={(params) => (
            <TextField {...params} label="search for a player" />
          )}
        />
      </Stack>
    </ThemeProvider>
  );
}

export default LiveSearch;
