import React from "react";
import { ThemeProvider, createTheme } from "@mui/material/styles";
import CssBaseline from "@mui/material/CssBaseline";
import { grey } from "@mui/material/colors";
import {
  Button,
  ButtonGroup,
  Container,
  Grid,
} from "@mui/material";
import Code from "./components/Code";
import { DataSQL } from "./dataSQL";
import { useState } from "react";
import LiveSearch from "./components/LiveSearch";
import Header from "./components/Header";

const darkTheme = createTheme({
  palette: { primary: { main: grey[50] }, secondary: { main: grey[900] } },
});

export default function App() {
  const [type, setType] = useState("all");
  const [searchValue, setSearchValue] = useState(null);
  let Data = DataSQL;

  function handleType(text) {
    setType(() => text);
  }

  if (searchValue) {
    Data = [searchValue];
  }

  return (
    <>
      <ThemeProvider theme={darkTheme}>
        <CssBaseline />

        <Header/>
        <LiveSearch setValue={setSearchValue} />

        <Container align="center" sx={{ marginBottom: 10 }}>
          <ButtonGroup
            variant="outlined"
            aria-label="outlined primary button group"
            color="secondary"
            size="small"
          >
            <Button onClick={() => handleType("all")}>ALL</Button>
            <Button onClick={() => handleType(1)}>CYCLE-1 P1</Button>
            <Button onClick={() => handleType(1.5)}>CYCLE-1 P2</Button>
            <Button onClick={() => handleType(2)}>CYCLE-2 P1</Button>
            <Button onClick={() => handleType(2.5)}>CYCLE-2 P2</Button>
            <Button onClick={() => handleType(3)}>CYCLE-3</Button>
          </ButtonGroup>
        </Container>
      </ThemeProvider>
      {searchValue?      
      <Grid container spacing={2} style={{ alignItems: "stretch" }}>
        {Data.map((item) => {
            return (
              <Grid
                item
                md={12}
                lg={6}
                sx={{ maxWidth: "100%", margin: "auto" }}
              >
                <Code title={item.title} code={item.code} />
              </Grid>
            );
        })}
      </Grid>:
      <Grid container spacing={2} style={{ alignItems: "stretch" }}>
        {Data.map((item) => {
          if (type === "all")
            return (
              <Grid
                item
                md={12}
                lg={6}
                sx={{ maxWidth: "100%", margin: "auto" }}
              >
                <Code title={item.title} code={item.code} />
              </Grid>
            );

          if (type === item.cycle)
            return (
              <Grid
                item
                md={12}
                lg={6}
                sx={{ maxWidth: "100%", margin: "auto" }}
              >
                <Code title={item.title} code={item.code} />
              </Grid>
            );
        })}
      </Grid>
      }
    </>
  );
}
