import { ThemeProvider, createTheme } from "@mui/material/styles";
import CssBaseline from "@mui/material/CssBaseline";
import { grey } from "@mui/material/colors";
import {
  Button,
  Typography,
  ButtonGroup,
  Container,
  Grid,
} from "@mui/material";
import Code from "./components/Code";
import { Data } from "./data";
import { useState } from "react";

const darkTheme = createTheme({
  palette: { primary: { main: grey[50] }, secondary: { main: grey[900] } },
  typography: {
    // fontFamily: ["Syne", "sans-serif"].join(","),
  },
});

export default function App() {

  const[type, setType] = useState('all')

  function handleType(text){
    setType(() => text)
  }

  return (
    <>
      <ThemeProvider theme={darkTheme}>
        <CssBaseline />
        <main>
          <Typography
            variant="h3"
            color="secondary"
            level="title-sm"
            fontWeight={700}
            fontFamily={"Syne"}
            align="center"
            paddingTop={10}
            marginBottom={5}
          >
            WDD LAB PROGRAMS
          </Typography>
        </main>

        <Container align="center" sx={{ marginBottom: 10 }}>
          <ButtonGroup
            variant="outlined"
            aria-label="outlined primary button group"
            color="secondary"
          >
            <Button >ALL</Button>
            <Button>HTML</Button>
            <Button>CSS</Button>
            <Button>JS</Button>
          </ButtonGroup>
        </Container>

      </ThemeProvider>

      <Grid container spacing={2} style={{alignItems:'stretch'}}>
        {Data.map((item) => (
          <Grid item md={12} lg={6} sx={{maxWidth: '100%', margin:'auto'}}>
            <Code title={item.title} code={item.code}/>
          </Grid>
        ))}
      </Grid>
    </>
  );
}
